package Basics;

public class doWhile {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int i = 10;
		do{
			System.out.println("Value of i in doWhileloop " + i);
			i++;
		}while(i<5);
		

			
	}

}

