package Basics;

public class forLoop {
	
	public void example(){
		for(int i=0; i<5; i++){
			System.out.println("Value of i is " + i);
		}
	}



	public static void main(String[] args) {
		// TODO Auto-generated method stub
		forLoop ob = new forLoop();
		ob.example();
	}

}

